import React, { useState } from 'react';
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import CircularProgress from '@mui/material/CircularProgress';
import axios from 'axios';

const GenerateEmails = () => {
    const [email, setEmail] = useState('');
    const [purpose, setPurpose] = useState('');
    const [response, setResponse] = useState('');
    const [loading, setLoading] = useState(false);

    const fetchData = async () => {
        setLoading(true);
        try {
            const res = await axios.post('/api/generate-email', { purpose });
            const generatedEmail = res.data.choices[0].message.content;
            setResponse(generatedEmail);
            setLoading(false);

            await axios.post('/api/send-email', { email, content: generatedEmail });
        } catch (error) {
            console.error(error);
            setLoading(false);
        }
    };

    return (
        <Box
            sx={{
                display: 'flex',
                flexDirection: 'column',
                minHeight: '30vh',
                padding: 4,
                backgroundColor: '#f5f5f5',
            }}
        >
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'flex-start',
                }}
            >
                <Typography variant="h4" mb={4}>
                    What email would you like to send?
                </Typography>
                <Box component="form" width={1}>
                    <TextField
                        label="Recipient Email"
                        fullWidth
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        sx={{ mb: 2 }}
                    />
                    <TextField
                        label="Email Purpose"
                        fullWidth
                        value={purpose}
                        onChange={(e) => setPurpose(e.target.value)}
                    />
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={fetchData}
                        sx={{ mt: 2 }}
                    >
                        Submit
                    </Button>
                </Box>
            </Box>
            <Box
                sx={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'center',
                    justifyContent: 'center',
                    flexGrow: 1,
                }}
            >
                <Typography variant="subtitle1">
                    Response from ChatGPT:
                </Typography>
                {loading ? (
                    <CircularProgress />
                ) : (
                    <Typography variant="body1" mt={2}>
                        {response}
                    </Typography>
                )}
            </Box>
        </Box>
    );
};

export default GenerateEmails;
